INSERT INTO site_introduces (site_id,title,user_id, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES (85,122,'','','<p><span style=\"color:#B22222;\"><strong>GIỚI THIỆU</strong></span></p>\r\n\r\n<p>Từ xa xưa, trang sức đã luôn là biểu tượng của sự phú quý. Ngày nay trang sức vẫn giữ nguyên giá trị của mình, không những thế nó còn là biểu tượng của thời trang và đẳng cấp. Trang sức giúp cho người dùng thêm tự tin tỏa sáng trong mắt mọi người.</p>\r\n\r\n<p>Với sự phát triển không ngừng của xã hội, người tiêu dùng ngày càng trở lên tinh tế hơn trong việc lựa chọn trang sức để làm đẹp cho bản thân và hướng tới sự hoàn thiện. Hiểu được nhu cầu đó, Trang sức Ngọc Lan đã ra đời và không ngừng cải tiến, hoàn thiện các mẫu mã trang sức để đáp ứng mọi nhu cầu làm đẹp của khách hàng.</p>\r\n\r\n<p>Thương hiệu trang sức Ngọc Lan được khởi nguồn từ năm 1995 do Công ty TNHH thương mại vàng Ngọc Lan thành lập. Trang sức Ngọc Lan đã mang đến một phong cách hoàn toàn mới về thiết kế, công nghệ sản xuất hoàn chỉnh và chính sách bán hàng tiên phong góp phần vào sự phát triển của lĩnh vực trang sức Hà Nội nói riêng và trang sức Việt Nam nói chung.</p>\r\n\r\n<p>Gần 20 năm trên thị trường, trang sức Ngọc Lan hãnh diện là một trong những thương hiệu hàng đầu về trang sức, thỏa mãn được mọi nhu cầu làm đẹp của các quý bà, quý cô và các quý ông lịch lãm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222;\"><strong>SỨ MỆNH</strong></span></p>\r\n\r\n<p>Chúng tôi hiểu rằng khách hàng của mình là những người am hiểu trang sức và có gu thẩm mỹ cao, luôn say mê tìm kiếm những sản phẩm mới, độc đáo và sang trọng.</p>\r\n\r\n<p>Đáp ứng nhu cầu đó, trang sức Ngọc Lan luôn tiên phong trong sứ mệnh sáng tạo các tác phẩm có giá trị thẩm mỹ cao, mẫu mã phong phú, độc đáo và hợp thời trang.</p>\r\n\r\n<p>Với mong muốn mang đến cho khách hàng sản phẩm và dịch vụ tốt nhất, trang sức Ngọc Lan sẽ tư vấn cho khách hàng sản phẩm phù hợp , chế độ bảo hành vĩnh viễn, hệ thống cửa hàng tiện lợi và chăm sóc chu đáo, nhiệt tình</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222;\"><strong>MỤC TIÊU DÀI HẠN</strong></span></p>\r\n\r\n<p>Trang sức Ngọc Lan mong muốn trở thành một trong những nhà cung cấp trang sức hàng đầu và uy tín tại Việt Nam. Chúng tôi đã và đang nỗ lực hết mình để chinh phục khách hàng bằng chất lượng sản phẩm, mẫu mã độc đáo và phong phú cũng như phong cách phục vụ hoàn hảo. Đến với trang sức Ngọc Lan bạn sẽ được thỏa mãn mọi nhu cầu làm đẹp và hài lòng với dịch vụ của chúng tôi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222;\"><strong>HOẠT ĐỘNG CỘNG ĐỒNG</strong></span></p>\r\n\r\n<p>Không chỉ mang đến cho khách hàng sản phẩm – dịch vụ tốt nhất, trang sức Ngọc Lan còn thể hiện trách nhiệm xã hội của mình thông qua các hoạt động cộng đồng như:</p>\r\n\r\n<p>- Tham gia và giành HCV hội chợ nữ trang Việt Nam với sản phẩm \"Dòng thời gian\".</p>\r\n\r\n<p>- Góp sức tạo nên thành công của Truyền thông tiếp thị Việt Nam đón chào huyền thoại Marketing Phillip Kotler đến Việt Nam.</p>\r\n\r\n<p>- Tài trợ bạc của chương trình doanh nhân Việt Nam 2007.</p>\r\n\r\n<p>Trang sức Ngọc Lan sẽ còn nỗ lực hơn nữa để tham gia nhiều sự kiện và góp phần tích cực vào sự phát triển chung của xã hội.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222;\"><strong>LỜI CẢM ƠN</strong></span></p>\r\n\r\n<p>Chúng tôi xin cảm ơn sự tin tưởng và ủng hộ của quý khách trong thời gian qua. Kính chúc quý khách sức khỏe và thịnh vượng</p>\r\n\r\n<p>Đến với trang sức Ngọc Lan, bạn sẽ cảm nhận rõ sự khác biệt và hài lòng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222;\"><strong>HỆ THỐNG CỬA HÀNG</strong></span></p>\r\n\r\n<p>​<img alt=\"\" height=\"290\" src=\"http://trangsucngoclan.com/uploads/images/gioithieu/shop_hang%20bong1.jpg\" style=\"margin: 0px; padding: 0px; outline: 0px none; vertical-align: baseline; text-indent: 0px; font-size: 12px; font-family: Arial; list-style-type: none; border: 0px none; color: rgb(254, 254, 254); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; text-align: center; text-transform: none; white-space: normal; word-spacing: 0px; background: none repeat scroll 0% 0% transparent;\" width=\"476\" /></p>\r\n\r\n<p>Số 93 Hàng Bông, Quận Hoàn Kiếm, Hà Nội<br />\r\nĐT: 84 - 3 944 9259</p>\r\n','','','','','',1425296058,1425296058);INSERT INTO maps (id,site_id, user_id, latlng, `name`, address, email, phone, website, headoffice, `order`, created_time, modified_time) VALUES (null,85,122,'21.03004,105.846668','CÔNG TY TNHH THƯƠNG MẠI VÀNG NGỌC LAN','93 Hàng Bông, Hoàn Kiếm, Hà Nội','','0919.718.538','kimcuongngoclan.vn',1,0,1425296058,1425296058);INSERT INTO banner_groups (banner_group_id, banner_group_name, banner_group_description, site_id, user_id, width, height, created_time)  VALUES (null, 'Nhóm chính', 'các banner chính', '85', '122', '0', '386', '1425296058');INSERT INTO banner_groups (banner_group_id, banner_group_name, banner_group_description, site_id, user_id, width, height, created_time)  VALUES (null, 'Nhóm đối tác', 'đối tác', '85', '122', '0', '24', '1425296058');INSERT INTO banners (banner_id, site_id, banner_group_id, banner_name, banner_description, banner_src, banner_width, banner_height, banner_link, banner_type, banner_order, banner_rules, banner_target, created_time, banner_showall) VALUES INSERT INTO news_categories (cat_id, site_id, user_id, cat_parent, cat_name, `alias`, cat_order, cat_description, cat_countchild, image_path, image_name, `status`, created_time, modified_time, showinhome, meta_keywords, meta_description) VALUES (null,85,122,0,'Bản tin Ngọc Lan','ban-tin-ngoc-lan',24,'',0,'','',1,1425296058, 1425296058,0,'','');INSERT INTO news_categories (cat_id, site_id, user_id, cat_parent, cat_name, `alias`, cat_order, cat_description, cat_countchild, image_path, image_name, `status`, crea